import { Component, OnInit } from '@angular/core';
import { AlertController, NavController, ToastController } from '@ionic/angular';
import { GroceriesService } from '../services/groceries.service';
import { ModalControllerService } from '../services/modal-controller.service';
import { SocialSharing } from '@awesome-cordova-plugins/social-sharing/ngx';

@Component({
  selector: 'app-home',
  templateUrl: './home.page.html',
  styleUrls: ['./home.page.scss'],
})
export class HomePage implements OnInit {
  title = "Grocery";

  constructor(public toastCtrl: ToastController,private socialSharing: SocialSharing, public groceriesService: GroceriesService, public modalControllerService:ModalControllerService) {

  }
ngOnInit(): void {

}
  loadItems(){
    return this.groceriesService.getItems();
  }

 async removeItem(item:any, index:any) {
    console.log("Remove Item - ", item, index);
    const toast =await this.toastCtrl.create({
      message: 'Removing Item - ' + index + "...",
      duration: 3000
    });
    toast.present();
    this.groceriesService.removeItem(index);

  }

  async shareItem(item:any, index:any) {
    console.log("Share Item - ", item, index);
    const toast = await this.toastCtrl.create({
      message: 'Sharing Item - ' + index + "...",
      duration: 3000
    });
    toast.present(); 

    let message = "Grocery Item - Name: " + item.name  + " - Quantity: " + item.quatity;
    let subject = "Share via Groceries app";

    this.socialSharing.share(message, subject).then(() => {
      console.log("Shared succdefully!")
    }).catch((error) => {
      console.error("Error while sharing", error);
    });

  }

  async editItem(item:any, index:any) {
    console.log("Edit Item - ", item, index);
    const toast = await this.toastCtrl.create({
      message: 'Edittitng Item - ' + index + "...",
      duration: 3000
    });
    toast.present();
    this.modalControllerService.showPrompot(item, index);

  }

  addItem() {
    console.log("Adding Item");
    this.modalControllerService.showPrompot();
  }



}
<ion-header>
  <ion-toolbar>
    <ion-title>Home</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <h3 class="no-item" text-center *ngIf="loadItems().length ===0">
    No Items available on the list!
  </h3>

  <ion-list>
    <ion-item-sliding *ngFor="let item of loadItems(), let i = index">
      <ion-list>
        <ion-item>
          <ion-label> {{item.name}}</ion-label>

          <ion-text slot="end">{{item.quantity}}</ion-text>
        </ion-item>
      </ion-list>
      <ion-item-options>
        <ion-button (click)="editItem(item, i)"

          color="primary"
          icon-start
        >
          <ion-icon  color="primary" name="create"> </ion-icon>
          Edit
        </ion-button>

        <ion-button
          (click)="shareItem(item, i)"
          color="light"
          class ='icon-start'
        >
          <ion-icon name="share"> </ion-icon>
          Share
        </ion-button>

        <ion-button
          (click)="removeItem(item, i)"
          color="secondary"
          class ='icon-start'
        >
          <ion-icon name="trash"> </ion-icon>
          Done
        </ion-button>
      </ion-item-options>
    </ion-item-sliding>
  </ion-list>
  <ion-fab vertical="bottom" horizontal="end" slot="fixed">
    <ion-fab-button (click)="addItem()">
      <ion-icon name="add"></ion-icon>
    </ion-fab-button>
  </ion-fab>
</ion-content>
