import { Component, OnInit } from '@angular/core';
import { AlertController, NavController, ToastController } from '@ionic/angular';
import { GroceriesService } from '../services/groceries.service';
import { ModalControllerService } from '../services/modal-controller.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.page.html',
  styleUrls: ['./home.page.scss'],
})
export class HomePage implements OnInit {
  title = "Grocery";

  constructor(public toastCtrl: ToastController, public groceriesService: GroceriesService, public modalControllerService:ModalControllerService) {

  }
ngOnInit(): void {

}
  loadItems(){
    return this.groceriesService.getItems();
  }

 async removeItem(item:any, index:any) {
    console.log("Remove Item - ", item, index);
    const toast =await this.toastCtrl.create({
      message: 'Removing Item - ' + index + "...",
      duration: 3000
    });
    toast.present();
    this.groceriesService.removeItem(index);

  }

  async shareItem(item:any, index:any) {
    console.log("Share Item - ", item, index);
    const toast = await this.toastCtrl.create({
      message: 'Sharing Item - ' + index + "...",
      duration: 3000
    });
    toast.present(); 

    let message = "Grocery Item - Name: " + item.name  + " - Quantity: " + item.quatity;
    let subject = "Share via Groceries app";

    // this.socialSharing.share(message, subject).then(() => {
    //   console.log("Shared succdefully!")
    // }).catch((error) => {
    //   console.error("Error while sharing", error);
    // });

  }

  async editItem(item:any, index:any) {
    console.log("Edit Item - ", item, index);
    const toast = await this.toastCtrl.create({
      message: 'Edittitng Item - ' + index + "...",
      duration: 3000
    });
    toast.present();
    this.modalControllerService.showPrompot(item, index);

  }

  addItem() {
    console.log("Adding Item");
    this.modalControllerService.showPrompot();
  }



}
<ion-header>
  <ion-toolbar>
    <ion-title>Home</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <h3 class="no-item" text-center
    *ngIf="loadItems().length ===0">No
    Items available on the list!</h3>
  <ion-list>
    <ion-item-sliding
      *ngFor="let item of loadItems(), let i = index">
      <ion-item>
        <h2>{{item.name}}</h2>
        <p>{{item.quantity}}</p>
      </ion-item>
      <ion-item-options>
        <button
          (click)="editItem(item, i)"
          ion-button color="primary"
          icon-start>
          <ion-icon name="create">
          </ion-icon>
          Edit
        </button>

        <button
          (click)="shareItem(item, i)"
          ion-button color="light"
          icon-start>
          <ion-icon name="share">
          </ion-icon>
          share
        </button>

        <button
          (click)="removeItem(item, i)"
          ion-button color="secondary"
          icon-start>
          <ion-icon name="trash">
          </ion-icon>
          Done
        </button>
      </ion-item-options>
    </ion-item-sliding>
  </ion-list>
  <ion-fab bottom right>
    <button (click)="addItem()" ion-fab
      mini>
      <ion-icon name="add"></ion-icon>
    </button>
  </ion-fab>

</ion-content>
